package com.theyawns.ecommerce.account.exception;

/**
 * Exception thrown when a customer cannot be found.
 *
 * @author Generated by Claude Code
 * @since 1.0
 */
public class CustomerNotFoundException extends RuntimeException {

    private static final long serialVersionUID = 1L;

    private final String customerId;

    /**
     * Creates a new CustomerNotFoundException.
     *
     * @param customerId the ID of the customer that was not found
     */
    public CustomerNotFoundException(String customerId) {
        super("Customer not found: " + customerId);
        this.customerId = customerId;
    }

    /**
     * Returns the customer ID that was not found.
     *
     * @return the customer ID
     */
    public String getCustomerId() {
        return customerId;
    }
}
