package com.theyawns.ecommerce.common.dto;

import jakarta.validation.constraints.DecimalMin;
import jakarta.validation.constraints.Min;
import jakarta.validation.constraints.NotBlank;

import java.io.Serializable;
import java.math.BigDecimal;
import java.util.Objects;

/**
 * Data Transfer Object for Order Line Item information.
 * Represents a single item within an order.
 *
 * @author Generated by Claude Code
 * @since 1.0
 */
public class OrderLineItemDTO implements Serializable {

    private static final long serialVersionUID = 1L;

    @NotBlank(message = "Product ID is required")
    private String productId;

    private String productName;

    private String sku;

    @Min(value = 1, message = "Quantity must be at least 1")
    private int quantity;

    @DecimalMin(value = "0.01", message = "Unit price must be at least 0.01")
    private BigDecimal unitPrice;

    private BigDecimal lineTotal;

    /**
     * Default constructor.
     */
    public OrderLineItemDTO() {
    }

    /**
     * Constructor with required fields.
     *
     * @param productId the product ID
     * @param quantity the quantity ordered
     * @param unitPrice the price per unit
     */
    public OrderLineItemDTO(String productId, int quantity, BigDecimal unitPrice) {
        this.productId = productId;
        this.quantity = quantity;
        this.unitPrice = unitPrice;
        this.lineTotal = unitPrice.multiply(BigDecimal.valueOf(quantity));
    }

    /**
     * Full constructor.
     *
     * @param productId the product ID
     * @param productName the product name
     * @param sku the product SKU
     * @param quantity the quantity ordered
     * @param unitPrice the price per unit
     */
    public OrderLineItemDTO(String productId, String productName, String sku,
                            int quantity, BigDecimal unitPrice) {
        this.productId = productId;
        this.productName = productName;
        this.sku = sku;
        this.quantity = quantity;
        this.unitPrice = unitPrice;
        this.lineTotal = unitPrice.multiply(BigDecimal.valueOf(quantity));
    }

    /**
     * Calculates and returns the line total.
     *
     * @return line total (quantity * unit price)
     */
    public BigDecimal calculateLineTotal() {
        if (unitPrice == null) {
            return BigDecimal.ZERO;
        }
        return unitPrice.multiply(BigDecimal.valueOf(quantity));
    }

    // Getters and Setters

    public String getProductId() {
        return productId;
    }

    public void setProductId(String productId) {
        this.productId = productId;
    }

    public String getProductName() {
        return productName;
    }

    public void setProductName(String productName) {
        this.productName = productName;
    }

    public String getSku() {
        return sku;
    }

    public void setSku(String sku) {
        this.sku = sku;
    }

    public int getQuantity() {
        return quantity;
    }

    public void setQuantity(int quantity) {
        this.quantity = quantity;
        this.lineTotal = calculateLineTotal();
    }

    public BigDecimal getUnitPrice() {
        return unitPrice;
    }

    public void setUnitPrice(BigDecimal unitPrice) {
        this.unitPrice = unitPrice;
        this.lineTotal = calculateLineTotal();
    }

    public BigDecimal getLineTotal() {
        return lineTotal != null ? lineTotal : calculateLineTotal();
    }

    public void setLineTotal(BigDecimal lineTotal) {
        this.lineTotal = lineTotal;
    }

    @Override
    public boolean equals(Object o) {
        if (this == o) return true;
        if (o == null || getClass() != o.getClass()) return false;
        OrderLineItemDTO that = (OrderLineItemDTO) o;
        return Objects.equals(productId, that.productId);
    }

    @Override
    public int hashCode() {
        return Objects.hash(productId);
    }

    @Override
    public String toString() {
        return "OrderLineItemDTO{" +
                "productId='" + productId + '\'' +
                ", productName='" + productName + '\'' +
                ", quantity=" + quantity +
                ", unitPrice=" + unitPrice +
                ", lineTotal=" + getLineTotal() +
                '}';
    }
}
